mapper.select:
SELECT PAYMENT_CODE, USERID, SEND_NAME, COUPON_CODE, PAYMENT_METHOD, PAYMENT_STATUS, TOTALPRICE, RDATE FROM (SELECT ROW_NUMBER()OVER(ORDER BY P.RDATE DESC) AS RNUM, P.PAYMENT_CODE, P.USERID, P.SEND_NAME, P.COUPON_CODE, P.PAYMENT_METHOD, P.PAYMENT_STATUS, P.TOTALPRICE, P.RDATE FROM RHY_PAYMENT P, RHY_PAYMENT_DETAILS PD, RHY_STORE_STOCK SS, RHY_STORE_PRODUCT SP, RHY_MEM_C MC WHERE P.PAYMENT_CODE=PD.PAYMENT_CODE AND PD.S_SEQ=SS.STOCK_SEQ AND SS.P_SEQ=SP.P_SEQ AND SP.C_NAME=MC.C_NAME AND MC.ID=#{userid}
AND USERID LIKE '%' #{s_keyword} '%'
AND SEND_NAME LIKE '%' #{s_keyword} '%'
AND PAYMENT_METHOD LIKE '%' #{s_keyword} '%'
AND PAYMENT_STATUS LIKE '%' #{s_keyword} '%'
GROUP BY P.PAYMENT_CODE ORDER BY P.RDATE DESC ) B WHERE RNUM BETWEEN ${start} AND ${end}

SELECT C_NAME
FROM RHY_MEM_C
WHERE ID=#{userid}

SELECT * FROM RHY_PAYMENT P, RHY_PAYMENT_AFTER PA, RHY_PAYMENT_DETAILS PD, RHY_STORE_STOCK SS, RHY_STORE_PRODUCT SP, RHY_DELIVERY D, RHY_MEM_COUPON MC, RHY_MEM_COUPON_DETAIL MCD, RHY_MEM_C MCC WHERE P.PAYMENT_CODE = PA.PAYMENT_CODE AND P.PAYMENT_CODE = PD.PAYMENT_CODE AND P.PAYMENT_CODE = D.PAYMENT_CODE AND PD.S_SEQ = SS.STOCK_SEQ AND SS.P_SEQ = SP.P_SEQ AND SP.C_NAME = MCC.C_NAME AND MCC.ID=#{userid}
AND P.COUPON_CODE = MCD.COUP_CODE AND MCD.C_SEQ = MC.SEQ AND MCD.COUP_CODE=#{coupon_code}
AND P.PAYMENT_CODE LIKE CONCAT('%',#{payment_code},'%') GROUP BY SS.STOCK_SEQ

mapper.update:
UPDATE RHY_PAYMENT
SET PAYMENT_STATUS = '결제완료'
WHERE SEQ = #{seq}
