mapper.select:
SELECT P_SEQ, C_NAME, P_NAME, P_TITLE, P_PRICE, P_COLOR, NATION, MDATE, DETAIL, PHOTO1_FILE, PHOTO2_FILE, PHOTO3_FILE, PHOTO4_FILE, PHOTO5_FILE, C1_NAME 
FROM rhy_store_product
WHERE P_SEQ = #{p_seq}

SELECT STOCK_SEQ, SIZE, QUANTITY
FROM rhy_store_stock
WHERE P_SEQ = #{p_seq}

mapper.update:
UPDATE rhy_store_basket
SET P_QUANTITY = #{p_quantity}
WHERE STOCK_SEQ = #{stock_seq} AND ID = #{id}
